INSERT INTO products (name, description, thumbnail, image, category_id) VALUES ('Rollershades', 'Roller shades are one of the most popular styles of window coverings. Roller shades are also a great value so you can stay within budget, and simple to clean so that you can keep them looking brand new.', 'https://shadeomatic.com/wp-content/uploads/2020/04/B1-720-992-4.jpg', 'https://dc-test.myshadestudio.com/preview.php?retkey=ALTA&product=905113&fabric=P2000&color=P04&width=960&height=960&view=z1', 1);
INSERT INTO products (name, description, thumbnail, image, category_id) VALUES ('Dual-Concept', 'Dual shades are the perfect choice for many rooms and styles. Dual shades are made from one piece of fabric with alternating translucent and light-filtering material.', 'https://shadeomatic.com/wp-content/uploads/2020/04/B2-720-992-2.jpg', 'https://dc-test.myshadestudio.com/preview.php?retkey=ALTA&product=905090&fabric=DS10&color=2803&width=960&height=960&view=z1', 1);
INSERT INTO products (name, description, thumbnail, image, category_id) VALUES ('Faux Wood', 'Faux wood blinds are made from a variety of materials including composite, PVC, vinyl or foam wood. They include a wood grain and are stained to match wood colors. Manufacturers use the term “faux wood,” meaning a wood substitute to create the look and feel of true wood.', 'https://shadeomatic.com/wp-content/uploads/2020/04/B1-720-992-3.jpg', 'https://dc-test.myshadestudio.com/preview.php?retkey=ALTA&product=905067&fabric=PG2&color=PG500&width=960&height=960&view=z1', 1);
INSERT INTO products (name, description, thumbnail, image, category_id) VALUES ('Vertical Blinds', 'Offering a selection of patterns, colours and textures, in fabric and PVC vanes. Vertical blinds offer total light control, easy maintenance, flexibility, and add style and elegance to any room.', 'https://shadeomatic.com/wp-content/uploads/2020/03/1-1920-1024x656.jpg', 'https://dc-test.myshadestudio.com/preview.php?retkey=ALTA&product=905092&fabric=V03&color=03&width=960&height=960&view=z1', 1);